              <?= __('All posts', 'blog')?>
            
          </h2>
        </a>
        <ul class="posts__list posts__list--main js-posts__list">
          <?php
            
            $all_args = array(
                'paged'          => $all,
                'posts_per_page' => 10,
                'orderby' => 'modified'
            );

            $all_query = new WP_Query( $all_args );
            
            while ( $all_query->have_posts() ) : $all_query->the_post();
            ?>
              <li class="posts__item">
             
                <a class="posts__item-link" href="<?php echo getRuLinkPost(get_the_ID()); ?>">
              
                  <div class="posts__img-wrapper">
                    <?php the_post_thumbnail('post-thumbnail', array('class'=>'posts__img')); ?>
                  </div>
                  <div class="posts__wrapper">
                    <time class="posts__item-text" datetime="<?php echo get_the_modified_date('Y-m-d') ?>">
                      <?php echo get_the_modified_date('F j, Y'); ?>
                    </time>
                    <h3 class="posts__item-title">
                      <?php the_title(); ?>
                    </h3>
                    <p class="posts__item-more-text">
                      <?php _e('Read more', 'blog') ?>
                    </p>
                  </div>
                </a>
              </li>
            <?php
            endwhile;?>
          </ul>
          <div class="our-blog__main-pagination pagination">
            
            <?php
            global $all_query;

            // Needed for first page only
            if ( ! $all_query->is_paged) {
                $all_posts_except_fp = ( $all_query->found_posts - 4 ); // Get us the found posts except those on first page
                $all_query->max_num_pages = ceil( $all_posts_except_fp / 10 ) + 1; // + 1 the first page we have containing 7 posts
            }
              $all_pag_args = array(
                  'format'  => '?all=%#%#all-anchor',
                  'current' => $all,
                  'total'   => $all_query->max_num_pages,
                  'prev_text' => __($pagination_arrow_left,'multi'),
                  'next_text' => __($pagination_arrow_right,'multi')
              );
              echo paginate_links( $all_pag_args );
            ?>
        </div>
              <?php if (  $all_query->max_num_pages > 1 ){?>
                
                <div class='load-more-wrapper'>
                 <button class='load-more-btn' id='load-more'>
